import React from "react";
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import ScrollTop from "../ui/scroll-top/scroll-top";
import PageWrapper from "../layout/page-wrapper/page-wrapper";
import styles from "./styles.module.css";
import news from "../mocks/news";
import comments from "../mocks/comments"
import MainPage from "components/pages/main-page/main-page";
import CommentPage from "components/pages/comment-page/comment-page";

function App() {
  return (
    <>
      <div className={styles.bodyWrapper}>
      <BrowserRouter>
        <ScrollTop />
        <Routes>
          <Route path="/" element={<MainPage news={news} />} />
          <Route path="product">
            <Route path=":code" element={<CommentPage comments={comments} />} />
          </Route>
        </Routes>
      </BrowserRouter>
      </div>
    </>
  );
}

export default App;
